import React,{Component} from 'react';
import ListadoIdiomas from './sections/listas'
import logo from './logo.svg';
import './App.css';



class App extends Component {
  render() {
    return (
    <div>
      <h4>Formularios</h4>
      <form>
        <p>
          <label> Nombre: </label>
          <input 
          id='name'
          name ='userName' 
          placeholder='Introduce el nombre'/>
        </p>
        <p>
          <label> Edad: </label>
          <input id='edad'
          name='edad'
          placeholder='Introduce tu edad'/>
        </p>
        <button>Enviar</button>
      </form> 

    </div>

  );
}
}

export default App;
